mapper.select:
     select * from (
            select ROW_NUMBER() OVER(order by CATEGORY,NEXTLEVEL_CATEGORY) as RN,
                ID, TITLE, TAG_TIME, LAST_TIME, HOLDERNAME, CATEGORY, NEXTLEVEL_CATEGORY, CONTENT
            from BLOG 
            where holdername=#{username} 
	             AND category=#{category}   
	             AND NEXTLEVEL_CATEGORY=#{nextlevelCategory}  
	               AND TITLE like '%'||#{title}||'%'  
            )
     where RN between #{startNumber} and #{endNumber}
